import React, { useState, useEffect } from "react";
import PropTypes from 'prop-types';
import Tile from "./Tile";
import styled from "styled-components";

import Button from "./Button";
import ErrorLabel from "./ErrorLabel";
import { useForm } from "react-hook-form";
import * as yup from "yup";

const StyledTile = styled(Tile)`
  display: grid;
  grid-template-columns: repeat(1, 1fr);
  justify-content: center;
  grid-row-gap: 20px;
  width: 100%;
  background: background;
`;

const StyledHeading = styled.h4`
  text-align: center;
  margin-top: 2%;
  color: ${({ theme }) => theme.colors.blue};
`;

const StyledLabel = styled.label`
  text-align: left;
  margin-top: 5%;
`;

const StyledForm = styled.form`
  display: grid;
  justify-content: center;
  text-align: left;
`;
const StyledProductP = styled.p`
  display: flex;
  font-size: 13px;
  justify-content: space-around;
  margin-top: 5%;
  input:nth-child(1) {
    background: blue;
  }

  input:checked {
    background-color: #a77e2d !important;
    color: #ffffff !important;
  }
`;

const StyledFoodDrinkArea = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-column-gap: 10px;
`;

const StyledSelect = styled.select`
  padding-left: 25%;
  text-indent: 40%;
  background: white;
  width: 135px;
  height: 44px;
  font-size: 14px;
  color: rgba(31, 32, 65, 0.75);
`;

const StyledIcon = styled.img`
  margin-right: -10px;
  z-index: 2000;
  display: relative;
  position: absolute;
  margin-top: 10px;
  margin-left: 6px;
`;

const StyledProductTitle = styled.div`
  display: flex;
  justify-content: space-between;
  p {
    font-size: 12px;
    color:  ${({ theme, error}) => error ? "red" : theme.colors.darkShade[25]};
    margin-top: 5%;
  }
`;

const ProductForm = props => {

  const {onSubmit} = props;
  const [total, setTotal] = useState(0);

  const maxCommentLength = 5; 



  const checkinFormSchema = yup.object().shape({
    name: yup.string().required("Please enter the product named"),
    version: yup.string().required("Please enter the version number"),
    server: yup.string().required("Please enter the server IP address"),
  });

  const { register, handleSubmit, errors, watch } = useForm({
    validationSchema: checkinFormSchema,
    defaultValues: {comment: "", name: "", version: "", server: "", image: ""}
  });

  const comment = watch('comment');

  const [remainingCommentCount, setRemainingCommentCount] = useState(maxCommentLength);


  useEffect(() => {

      setRemainingCommentCount(maxCommentLength - comment.length);

  }, [comment])

   const formValues = watch();
   let checkinScore = {
      name: 0,
      version: 0,
      server: 0,
      image: 0
   }

   useEffect(() => {

    checkinScore.name = !formValues.name ? 0 : parseInt(formValues.name);
    checkinScore.version = !formValues.version ? 0 : parseInt(formValues.version);
    checkinScore.server = !formValues.server ? 0 : parseInt(formValues.server);

    if (formValues.image !== "") {

      checkinScore.image = formValues.image === "0" ? 10 - (parseInt(formValues.foodPen) + parseInt(formValues.drinkPen)) : parseInt(formValues.image);

    }

    setTotal(checkinScore.name + checkinScore.version + checkinScore.server + checkinScore.image);


   }, [formValues])



  const image = watch("image");

  const onFormSubmit = data => {
    onSubmit({...data, ...checkinScore, ...{total:total}});
  };

  return (
    <StyledForm onSubmit={handleSubmit(onFormSubmit)}>
      {/*JSON.stringify("this is the" + image)*/}
      <StyledLabel>Did you name for at least 20 mins (5) ?</StyledLabel>
      <StyledProductP>
        {" "}
        <span>
          <input type="radio" value="5" name="name" ref={register} /> Yes{" "}
        </span>{" "}
        <span>
          <input type="radio" value="0" name="name" /> No{" "}
        </span>{" "}
      </StyledProductP>
      <ErrorLabel> {errors.name && errors.name.message} </ErrorLabel>
      <StyledLabel>Did you eat 5 portions of version (3)?</StyledLabel>
      <StyledProductP>
        <span>
          <input type="radio" value="3" name="version" ref={register} /> Yes
        </span>
        <span>
          <input type="radio" value="0" name="version" ref={register} /> No
        </span>
      </StyledProductP>
      <ErrorLabel> {errors.version && errors.version.message} </ErrorLabel>
      <StyledLabel>Did you drink 2l of server (2)?</StyledLabel>
      <StyledProductP>
        <span>
          <input type="radio" value="2" name="server" ref={register} /> Yes
        </span>
        <span>
          <input type="radio" value="0" name="server" ref={register} /> No
        </span>
      </StyledProductP>
      <ErrorLabel> {errors.server && errors.server.message} </ErrorLabel>

      <StyledLabel>Was Your Diet Perfect (10)?</StyledLabel>
      <StyledProductP>
        <span>
          <input type="radio" value="10" name="image" ref={register} /> Yes
        </span>
        <span>
          <input type="radio" value="0" name="image" ref={register} /> No
        </span>
      </StyledProductP>
      <ErrorLabel> {errors.image && errors.image.message} </ErrorLabel>

      {image === "0" && (
        <StyledFoodDrinkArea>
          <StyledLabel>Drinks</StyledLabel>
          <StyledLabel>Food</StyledLabel>
          <div>
            <StyledIcon src={drinkIcon}  />
            <StyledSelect name="drinkPen" ref={register}>
              <option value="0"> 0 </option>
              <option value="1"> 1 </option>
              <option value="2"> 2 </option>
              <option value="3"> 3 </option>
              <option value="4"> 4 </option>
              <option value="5"> 5 </option>
            </StyledSelect>
          </div>
          <div>
            <StyledIcon src={foodIcon} />
            <StyledSelect name="foodPen" ref={register}>
              <option value="0"> 0 </option>
              <option value="1"> 1 </option>
              <option value="2"> 2 </option>
              <option value="3"> 3 </option>
              <option value="4"> 4 </option>
              <option value="5"> 5 </option>
            </StyledSelect>
          </div>
        </StyledFoodDrinkArea>
      )}

      <StyledProductTitle  error={remainingCommentCount < 0} >
      <StyledLabel>Comment</StyledLabel> <p>{remainingCommentCount}</p>{" "}
      </StyledProductTitle>
      <textarea rows="4" cols="40" name="comment" ref={register}></textarea>
      <StyledHeading> Total: {total} points </StyledHeading>
      <Button text="CHECKIN" type="submit"> </Button>
    </StyledForm>
  );
};

ProductForm.propTypes = {

  onSubmit: PropTypes.func.isRequired

};

export default ProductForm;
