import { useState} from "react";

function useAuth(liAuth) {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(true);

  const googleProvider = new liAuth.GoogleAuthProvider();

  liAuth().onAuthStateChanged(liUser => {
    setLoading(false);
    if (liUser) {
      setIsAuthenticated(true);
      setUser(liUser);
      return;
    }

    setIsAuthenticated(false);
  });

  const createEmailUser = (email, password) =>
    liAuth().createUserWithEmailAndPassword(email, password);

  const signInEmailUser = (email, password) =>
    liAuth().signInWithEmailAndPassword(email, password);

  const signInWithProvider = provider => {
    debugger;
    switch (provider) {
      case "google":
        liAuth().signInWithRedirect(googleProvider);
        break;

      default:
        throw new Error("unsupported provider");
    }
  };

  const signOut = () => liAuth().signOut();

  return {
    isAuthenticated,
    user,
    loading,
    createEmailUser,
    signInEmailUser,
    signOut,
    signInWithProvider
  };
}
export default useAuth;
